import "./env"; // Load environment variables first
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import "./env"; // Load environment variables first

import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { adsMonitoringService } from "./services/adsMonitoringService";
import { storage } from "./storage";
import { sqlViewerService } from "./services/sqlViewerService";
import { pingService } from "./services/pingService";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

// Test ADS Connection
app.post('/api/test-ads-connection', async (req, res) => {
  const { siteId, tagId } = req.body;
  if (!siteId || !tagId) {
    return res.status(400).json({ message: 'siteId and tagId are required' });
  }
  try {
    // We need to fetch the full site and tag objects to pass to the service
    const site = await storage.getSite(siteId);
    const tag = await storage.getSiteDatabaseTag(tagId);

    if (!site) {
      return res.status(404).json({ message: 'Site not found' });
    }
    if (!tag) {
      return res.status(404).json({ message: 'Tag not found' });
    }

    const result = await adsMonitoringService.testADSConnection(site, tag);
    res.status(200).json(result);
  } catch (error: any) {
    console.error(`[API] Error testing ADS connection for site ${siteId}:`, error);
    res.status(500).json({ success: false, error: error.message });
  }
});

(async () => {
  // Register all API routes and create the HTTP server
  const httpServer = await registerRoutes(app);

  // Error handling middleware
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";
    res.status(status).json({ message });
  });

  // Setup Vite for development or serve static files for production
  if (app.get("env") === "development") {
    await setupVite(app, httpServer);
  } else {
    serveStatic(app);
  }

  // Define the port
  const port = parseInt(process.env.PORT || '5000', 10);

  // Start the server and initialize services
  httpServer.listen(port, async () => {
    log(`Server listening on http://localhost:${port}`);

    // Test SQL Viewer Connection
    try {
      await sqlViewerService.testConnection();
    } catch (error) {
      console.error("[FATAL] Could not connect to the external SQL Server. The SQL Viewer will not be available.");
    }

    // Initialize and start monitoring services
    try {
      const allSites = await storage.getAllSites();
      const allTags = await storage.getSiteDatabaseTags();
      console.log(`[INIT] Fetched ${allSites.length} sites and ${allTags.length} tags for monitoring.`);
      adsMonitoringService.startMonitoringAllSites(allSites, allTags);
    } catch (error) {
      console.error("Failed to fetch initial data for monitoring:", error);
      process.exit(1); // Exit if we can't get monitoring data
    }

    pingService.startMonitoring();
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { pingService } from "./services/pingService";
import { adsMonitoringService } from "./services/adsMonitoringService";
import { sqlViewerService } from "./services/sqlViewerService";
import multer from "multer";
import path from "path";
import fs from "fs/promises";
import { 
  insertSiteSchema, 
  insertIpcManagementSchema, 
  insertVfdParameterSchema, 
  insertProgramBackupSchema,
  insertProjectSchema,
  insertPlcTagSchema,
  insertPlcTagHistorySchema,
  insertSiteDatabaseTagSchema,
  insertSiteDatabaseValueSchema,
  insertMbrRealtimeDataSchema,
  insertRoRealtimeDataSchema
} from "@shared/schema";

// Configure multer for file uploads
const upload = multer({
  dest: "uploads/",
  limits: {
    fileSize: 50 * 1024 * 1024, // 50MB limit
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Start ping monitoring service
  pingService.startMonitoring();

  // Dashboard metrics
  app.get("/api/dashboard/metrics", async (req, res) => {
    try {
      const metrics = await storage.getDashboardMetrics();
      res.json(metrics);
    } catch (error) {
      console.error("Error fetching dashboard metrics:", error);
      res.status(500).json({ message: "Failed to fetch dashboard metrics" });
    }
  });

  // Projects endpoints
  app.get("/api/projects", async (req, res) => {
    try {
      const projects = await storage.getAllProjects();
      res.json(projects);
    } catch (error) {
      console.error("Error fetching projects:", error);
      res.status(500).json({ message: "Failed to fetch projects" });
    }
  });

  app.get("/api/projects/:id", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      console.error("Error fetching project:", error);
      res.status(500).json({ message: "Failed to fetch project" });
    }
  });

  app.post("/api/projects", async (req, res) => {
    try {
      // Handle date string conversion before schema validation
      const bodyData = { ...req.body };
      if (bodyData.createdDate && typeof bodyData.createdDate === 'string') {
        bodyData.createdDate = new Date(bodyData.createdDate);
      }
      if (bodyData.planStartDate && typeof bodyData.planStartDate === 'string') {
        bodyData.planStartDate = new Date(bodyData.planStartDate);
      }

      const projectData = insertProjectSchema.parse(bodyData);
      const project = await storage.createProject(projectData);
      res.status(201).json(project);
    } catch (error) {
      console.error("Error creating project:", error);
      res.status(400).json({ message: "Invalid project data" });
    }
  });

  app.put("/api/projects/:id", async (req, res) => {
    try {
      const projectData = insertProjectSchema.partial().parse(req.body);
      const project = await storage.updateProject(req.params.id, projectData);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      console.error("Error updating project:", error);
      res.status(400).json({ message: "Invalid project data" });
    }
  });

  app.delete("/api/projects/:id", async (req, res) => {
    try {
      const success = await storage.deleteProject(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting project:", error);
      res.status(500).json({ message: "Failed to delete project" });
    }
  });

  // Sites endpoints
  app.get("/api/sites", async (req, res) => {
    try {
      const sites = await storage.getAllSites();
      res.json(sites);
    } catch (error) {
      console.error("Error fetching sites:", error);
      res.status(500).json({ message: "Failed to fetch sites" });
    }
  });

  app.get("/api/sites/:id", async (req, res) => {
    try {
      const site = await storage.getSite(req.params.id);
      if (!site) {
        return res.status(404).json({ message: "Site not found" });
      }
      res.json(site);
    } catch (error) {
      console.error("Error fetching site:", error);
      res.status(500).json({ message: "Failed to fetch site" });
    }
  });

  app.post("/api/sites", async (req, res) => {
    try {
      const siteData = insertSiteSchema.parse(req.body);
      const site = await storage.createSite(siteData);
      res.status(201).json(site);
    } catch (error) {
      console.error("Error creating site:", error);
      res.status(400).json({ message: "Invalid site data" });
    }
  });

  app.put("/api/sites/:id", async (req, res) => {
    try {
      const siteData = insertSiteSchema.partial().parse(req.body);
      const site = await storage.updateSite(req.params.id, siteData);
      if (!site) {
        return res.status(404).json({ message: "Site not found" });
      }
      res.json(site);
    } catch (error) {
      console.error("Error updating site:", error);
      res.status(400).json({ message: "Invalid site data" });
    }
  });

  app.delete("/api/sites/:id", async (req, res) => {
    try {
      const success = await storage.deleteSite(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Site not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting site:", error);
      res.status(500).json({ message: "Failed to delete site" });
    }
  });

  // Sync sites from IPC management
  app.post("/api/sites/sync-from-ipc", async (req, res) => {
    try {
      const ipcDevices = await storage.getIpcManagement();
      let existingSites = await storage.getAllSites();

      // Collect all valid IPs from IPC devices
      const validIPs = new Set<string>();
      for (const ipc of ipcDevices) {
        if (ipc.vpnIp) validIPs.add(ipc.vpnIp);
        if (ipc.lanIp) validIPs.add(ipc.lanIp);
      }

      let created = 0;
      let removed = 0;

      // Remove orphaned sites that no longer have corresponding IPC devices
      for (const site of existingSites) {
        if (!validIPs.has(site.ipAddress)) {
          await storage.deleteSite(site.id);
          removed++;
          console.log(`Removed orphaned site: ${site.name} (${site.ipAddress})`);
        }
      }

      // Refresh existing sites after deletions
      existingSites = await storage.getAllSites();
      const existingIPs = new Set(existingSites.map(site => site.ipAddress));

      // Create new sites for IPC devices
      for (const ipc of ipcDevices) {
        // Create sites for VPN IPs
        if (ipc.vpnIp && !existingIPs.has(ipc.vpnIp)) {
          try {
            await storage.createSite({
              name: `${ipc.deviceName} (VPN)`,
              description: `Monitoring ${ipc.deviceName} via VPN connection`,
              ipAddress: ipc.vpnIp,
              siteType: "production",
              location: null,
              isActive: true,
            });
            created++;
            existingIPs.add(ipc.vpnIp);
          } catch (error: any) {
            // Skip if duplicate (might be created by another sync operation)
            if (error.code !== '23505') {
              throw error;
            }
            console.log(`Site with VPN IP ${ipc.vpnIp} already exists, skipping...`);
          }
        }

        // Create sites for LAN IPs
        if (ipc.lanIp && !existingIPs.has(ipc.lanIp)) {
          try {
            await storage.createSite({
              name: `${ipc.deviceName} (LAN)`,
              description: `Monitoring ${ipc.deviceName} via LAN connection`,
              ipAddress: ipc.lanIp,
              siteType: "production", 
              location: null,
              isActive: true,
            });
            created++;
            existingIPs.add(ipc.lanIp);
          } catch (error: any) {
            // Skip if duplicate (might be created by another sync operation)
            if (error.code !== '23505') {
              throw error;
            }
            console.log(`Site with LAN IP ${ipc.lanIp} already exists, skipping...`);
          }
        }
      }

      res.json({ created, removed, total: ipcDevices.length });
    } catch (error) {
      console.error("Error syncing sites from IPC:", error);
      res.status(500).json({ message: "Failed to sync sites from IPC management" });
    }
  });

  // Ping endpoints
  app.post("/api/sites/:id/ping", async (req, res) => {
    try {
      const site = await storage.getSite(req.params.id);
      if (!site) {
        return res.status(404).json({ message: "Site not found" });
      }

      await pingService.checkSiteStatus(site.id, site.ipAddress, site.name);
      const updatedSite = await storage.getSite(req.params.id);
      res.json(updatedSite);
    } catch (error) {
      console.error("Error pinging site:", error);
      res.status(500).json({ message: "Failed to ping site" });
    }
  });

  app.post("/api/ping/all", async (req, res) => {
    try {
      await pingService.checkAllSites();
      res.json({ message: "Ping check initiated for all sites" });
    } catch (error) {
      console.error("Error pinging all sites:", error);
      res.status(500).json({ message: "Failed to ping all sites" });
    }
  });

  // Uptime history
  app.get("/api/sites/:id/uptime", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const history = await storage.getUptimeHistory(req.params.id, limit);
      res.json(history);
    } catch (error) {
      console.error("Error fetching uptime history:", error);
      res.status(500).json({ message: "Failed to fetch uptime history" });
    }
  });

  app.get("/api/sites/:id/uptime/stats", async (req, res) => {
    try {
      const hours = req.query.hours ? parseInt(req.query.hours as string) : 24;
      const stats = await storage.getUptimeStats(req.params.id, hours);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching uptime stats:", error);
      res.status(500).json({ message: "Failed to fetch uptime stats" });
    }
  });

  // Program backups
  app.get("/api/backups", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const backups = await storage.getProgramBackups(siteId);
      res.json(backups);
    } catch (error) {
      console.error("Error fetching backups:", error);
      res.status(500).json({ message: "Failed to fetch backups" });
    }
  });

  app.post("/api/backups", upload.single("file"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      const backupData = insertProgramBackupSchema.parse({
        ...req.body,
        fileName: req.file.originalname,
        filePath: req.file.path,
        fileSize: req.file.size,
      });

      const backup = await storage.createProgramBackup(backupData);
      res.status(201).json(backup);
    } catch (error) {
      console.error("Error creating backup:", error);
      res.status(400).json({ message: "Invalid backup data" });
    }
  });

  app.delete("/api/backups/:id", async (req, res) => {
    try {
      const success = await storage.deleteProgramBackup(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Backup not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting backup:", error);
      res.status(500).json({ message: "Failed to delete backup" });
    }
  });



  // IPC management
  app.get("/api/ipc-management", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const devices = await storage.getIpcManagement(siteId);
      // Remove actual passwords from response for security
      const safeDevices = devices.map(device => ({
        ...device,
        ipcPassword: device.ipcPassword ? "****" : undefined,
        anydeskPassword: device.anydeskPassword ? "****" : undefined,
      }));
      res.json(safeDevices);
    } catch (error) {
      console.error("Error fetching IPC devices:", error);
      res.status(500).json({ message: "Failed to fetch IPC devices" });
    }
  });

  app.post("/api/ipc-management", async (req, res) => {
    try {
      const deviceData = insertIpcManagementSchema.parse(req.body);
      const device = await storage.createIpcManagement(deviceData);
      // Remove passwords from response
      const safeDevice = { 
        ...device, 
        ipcPassword: device.ipcPassword ? "****" : undefined,
        anydeskPassword: device.anydeskPassword ? "****" : undefined,
      };
      res.status(201).json(safeDevice);
    } catch (error) {
      console.error("Error creating IPC device:", error);
      res.status(400).json({ message: "Invalid device data" });
    }
  });

  app.put("/api/ipc-management/:id", async (req, res) => {
    try {
      const deviceData = insertIpcManagementSchema.partial().parse(req.body);
      const device = await storage.updateIpcManagement(req.params.id, deviceData);
      if (!device) {
        return res.status(404).json({ message: "Device not found" });
      }
      // Remove passwords from response
      const safeDevice = { 
        ...device, 
        ipcPassword: device.ipcPassword ? "****" : undefined,
        anydeskPassword: device.anydeskPassword ? "****" : undefined,
      };
      res.json(safeDevice);
    } catch (error) {
      console.error("Error updating IPC device:", error);
      res.status(400).json({ message: "Invalid device data" });
    }
  });

  app.delete("/api/ipc-management/:id", async (req, res) => {
    try {
      // Get the IPC device details before deletion to know which IPs to clean up
      const ipcDevices = await storage.getIpcManagement();
      const deviceToDelete = ipcDevices.find(device => device.id === req.params.id);

      const success = await storage.deleteIpcManagement(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Device not found" });
      }

      // Clean up any monitoring sites that belong to this IPC device
      if (deviceToDelete) {
        const sitesToCleanup = [];
        const allSites = await storage.getAllSites();

        for (const site of allSites) {
          if ((deviceToDelete.vpnIp && site.ipAddress === deviceToDelete.vpnIp) ||
              (deviceToDelete.lanIp && site.ipAddress === deviceToDelete.lanIp)) {
            await storage.deleteSite(site.id);
            sitesToCleanup.push(site.name);
          }
        }

        if (sitesToCleanup.length > 0) {
          console.log(`Automatically removed ${sitesToCleanup.length} monitoring sites for deleted IPC device: ${sitesToCleanup.join(', ')}`);
        }
      }

      res.status(204).send();
    } catch (error) {
      console.error("Error deleting IPC device:", error);
      res.status(500).json({ message: "Failed to delete IPC device" });
    }
  });

  // VFD parameters
  app.get("/api/vfd-parameters", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const parameters = await storage.getVfdParameters(siteId);
      res.json(parameters);
    } catch (error) {
      console.error("Error fetching VFD parameters:", error);
      res.status(500).json({ message: "Failed to fetch VFD parameters" });
    }
  });

  app.post("/api/vfd-parameters", async (req, res) => {
    try {
      const parameterData = insertVfdParameterSchema.parse(req.body);
      const parameter = await storage.createVfdParameter(parameterData);
      res.status(201).json(parameter);
    } catch (error) {
      console.error("Error creating VFD parameter:", error);
      res.status(400).json({ message: "Invalid parameter data" });
    }
  });

  app.put("/api/vfd-parameters/:id", async (req, res) => {
    try {
      const parameterData = insertVfdParameterSchema.partial().parse(req.body);
      const parameter = await storage.updateVfdParameter(req.params.id, parameterData);
      if (!parameter) {
        return res.status(404).json({ message: "Parameter not found" });
      }
      res.json(parameter);
    } catch (error) {
      console.error("Error updating VFD parameter:", error);
      res.status(400).json({ message: "Invalid parameter data" });
    }
  });

  app.delete("/api/vfd-parameters/:id", async (req, res) => {
    try {
      const success = await storage.deleteVfdParameter(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Parameter not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting VFD parameter:", error);
      res.status(500).json({ message: "Failed to delete VFD parameter" });
    }
  });





  // Alerts
  app.get("/api/alerts", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
      const alerts = await storage.getAlerts(limit);
      res.json(alerts);
    } catch (error) {
      console.error("Error fetching alerts:", error);
      res.status(500).json({ message: "Failed to fetch alerts" });
    }
  });

  app.put("/api/alerts/:id/read", async (req, res) => {
    try {
      await storage.markAlertAsRead(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error marking alert as read:", error);
      res.status(500).json({ message: "Failed to mark alert as read" });
    }
  });

  app.put("/api/alerts/:id/resolve", async (req, res) => {
    try {
      await storage.markAlertAsResolved(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error marking alert as resolved:", error);
      res.status(500).json({ message: "Failed to mark alert as resolved" });
    }
  });

  app.get("/api/alerts/unread-count", async (req, res) => {
    try {
      const count = await storage.getUnreadAlertsCount();
      res.json({ count });
    } catch (error) {
      console.error("Error fetching unread alerts count:", error);
      res.status(500).json({ message: "Failed to fetch unread alerts count" });
    }
  });

  // PLC Tags
  app.get("/api/plc-tags", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const tags = await storage.getPlcTags(siteId);
      res.json(tags);
    } catch (error) {
      console.error("Error fetching PLC tags:", error);
      res.status(500).json({ message: "Failed to fetch PLC tags" });
    }
  });

  app.get("/api/plc-tags/active", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const tags = await storage.getActivePlcTags(siteId);
      res.json(tags);
    } catch (error) {
      console.error("Error fetching active PLC tags:", error);
      res.status(500).json({ message: "Failed to fetch active PLC tags" });
    }
  });

  app.post("/api/plc-tags", async (req, res) => {
    try {
      const result = insertPlcTagSchema.safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ 
          message: "Validation failed", 
          errors: result.error.errors 
        });
      }

      const tag = await storage.createPlcTag(result.data);
      res.status(201).json(tag);
    } catch (error) {
      console.error("Error creating PLC tag:", error);
      res.status(500).json({ message: "Failed to create PLC tag" });
    }
  });

  app.put("/api/plc-tags/:id", async (req, res) => {
    try {
      const result = insertPlcTagSchema.partial().safeParse(req.body);
      if (!result.success) {
        return res.status(400).json({ 
          message: "Validation failed", 
          errors: result.error.errors 
        });
      }

      const tag = await storage.updatePlcTag(req.params.id, result.data);
      if (!tag) {
        return res.status(404).json({ message: "PLC tag not found" });
      }
      res.json(tag);
    } catch (error) {
      console.error("Error updating PLC tag:", error);
      res.status(500).json({ message: "Failed to update PLC tag" });
    }
  });

  app.delete("/api/plc-tags/:id", async (req, res) => {
    try {
      const deleted = await storage.deletePlcTag(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "PLC tag not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting PLC tag:", error);
      res.status(500).json({ message: "Failed to delete PLC tag" });
    }
  });

  // Bulk upload PLC tags
  app.post("/api/plc-tags/bulk", async (req, res) => {
    try {
      const { siteId, tags } = req.body;

      if (!siteId || !Array.isArray(tags)) {
        return res.status(400).json({ message: "siteId and tags array are required" });
      }

      // Validate each tag before bulk insert
      const validatedTags = [];
      for (const tag of tags) {
        const tagWithDefaults = {
          ...tag,
          isActive: tag.isActive !== undefined ? tag.isActive : true,
          alarmOnTrue: tag.alarmOnTrue !== undefined ? tag.alarmOnTrue : true,
          alarmOnFalse: tag.alarmOnFalse !== undefined ? tag.alarmOnFalse : false,
          severityLevel: tag.severityLevel || 'warning',
          dataType: tag.dataType || 'BOOL'
        };

        const result = insertPlcTagSchema.omit({ siteId: true }).safeParse(tagWithDefaults);
        if (!result.success) {
          return res.status(400).json({ 
            message: "Validation failed for tag: " + tag.tagName, 
            errors: result.error.errors 
          });
        }
        validatedTags.push(result.data);
      }

      const createdTags = await storage.bulkCreatePlcTags(siteId, validatedTags);
      res.status(201).json(createdTags);
    } catch (error) {
      console.error("Error bulk creating PLC tags:", error);
      res.status(500).json({ message: "Failed to bulk create PLC tags" });
    }
  });

  // PLC Tag History
  app.get("/api/plc-tags/:id/history", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const history = await storage.getPlcTagHistory(req.params.id, limit);
      res.json(history);
    } catch (error) {
      console.error("Error fetching PLC tag history:", error);
      res.status(500).json({ message: "Failed to fetch PLC tag history" });
    }
  });

  // PLC Tag Value Update (for manual testing)
  app.put("/api/plc-tags/:id/value", async (req, res) => {
    try {
      const { value, createHistory = true } = req.body;
      if (typeof value !== 'string') {
        return res.status(400).json({ message: "Value must be a string" });
      }

      await storage.updatePlcTagValue(req.params.id, value, createHistory);
      res.status(204).send();
    } catch (error) {
      console.error("Error updating PLC tag value:", error);
      res.status(500).json({ message: "Failed to update PLC tag value" });
    }
  });

  // Site Database Tags
  app.get("/api/site-database-tags", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const tags = await storage.getSiteDatabaseTags(siteId);
      res.json(tags);
    } catch (error) {
      console.error("Error fetching site database tags:", error);
      res.status(500).json({ message: "Failed to fetch site database tags" });
    }
  });

  app.post("/api/site-database-tags", async (req, res) => {
    try {
      console.log("[API] Received request to create new ADS tag.");
      const tagData = insertSiteDatabaseTagSchema.parse(req.body);
      console.log("[API] Tag data parsed successfully.", tagData);

      // We don't need to validate here, as the monitoring service will log errors if it can't connect.
      // This also simplifies the logic and avoids potential double-reads.

      console.log("[API] Creating tag in database...");
      const tag = await storage.createSiteDatabaseTag(tagData);
      console.log("[API] Tag created successfully in DB:", tag);

      // After creating the tag, start monitoring it immediately if it's active
      if (tag.isActive) {
        console.log(`[API] Tag '${tag.tagName}' is active. Attempting to start monitoring...`);
        try {
          adsMonitoringService.startMonitoringForTag(tag);
          console.log("[API] Call to start monitoring completed.");
        } catch (monitorError) {
          console.error("[API] CRITICAL: Error occurred while trying to start monitoring for the new tag:", monitorError);
          // Even if monitoring fails to start, we should still return the created tag.
          // The error will be logged for debugging.
        }
      }

      console.log("[API] Sending 201 response.");
      // Cleanse the object before sending: Drizzle can return BigInts or other non-serializable types.
      const cleanTag = JSON.parse(JSON.stringify(tag));
      res.status(201).json(cleanTag);
    } catch (error) {
      console.error("[API] CRITICAL: An unexpected error occurred in the create tag route handler:", error);
      // Check if it's a validation error from Zod
      if (error instanceof (await import('zod')).ZodError) {
        return res.status(400).json({ message: "Invalid tag data", errors: error.errors });
      }
      res.status(500).json({ message: "An unexpected error occurred while creating the tag." });
    }
  });

  app.put("/api/site-database-tags/:id", async (req, res) => {
    try {
      const tagData = insertSiteDatabaseTagSchema.partial().parse(req.body);
      const tag = await storage.updateSiteDatabaseTag(req.params.id, tagData);
      if (!tag) {
        return res.status(404).json({ message: "Tag not found" });
      }
      res.json(tag);
    } catch (error) {
      console.error("Error updating site database tag:", error);
      res.status(400).json({ message: "Invalid tag data" });
    }
  });

  app.delete("/api/site-database-tags/:id", async (req, res) => {
    try {
      const success = await storage.deleteSiteDatabaseTag(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Tag not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting site database tag:", error);
      res.status(500).json({ message: "Failed to delete tag" });
    }
  });

  // Site Database Values
  app.get("/api/site-database-values/:tagId", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const values = await storage.getSiteDatabaseValues(req.params.tagId, limit);
      res.json(values);
    } catch (error) {
      console.error("Error fetching site database values:", error);
      res.status(500).json({ message: "Failed to fetch values" });
    }
  });

  app.post("/api/site-database-values", async (req, res) => {
    try {
      const valueData = insertSiteDatabaseValueSchema.parse(req.body);
      const value = await storage.createSiteDatabaseValue(valueData);
      res.status(201).json(value);
    } catch (error) {
      console.error("Error creating site database value:", error);
      res.status(400).json({ message: "Invalid value data" });
    }
  });

  // Get latest values for all tags in a site
  app.get("/api/sites/:siteId/database-values/latest", async (req, res) => {
    try {
      const values = await storage.getLatestSiteDatabaseValues(req.params.siteId);
      res.json(values);
    } catch (error) {
      console.error("Error fetching latest site database values:", error);
      res.status(500).json({ message: "Failed to fetch latest values" });
    }
  });

  // Real-time MBR Data endpoints
  app.get("/api/mbr-realtime-data", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const data = await storage.getMbrRealtimeData(siteId, limit);
      res.json(data);
    } catch (error) {
      console.error("Error fetching MBR realtime data:", error);
      res.status(500).json({ message: "Failed to fetch MBR realtime data" });
    }
  });

  app.post("/api/mbr-realtime-data", async (req, res) => {
    try {
      const dataToInsert = insertMbrRealtimeDataSchema.parse(req.body);
      const data = await storage.createMbrRealtimeData(dataToInsert);
      res.status(201).json(data);
    } catch (error) {
      console.error("Error creating MBR realtime data:", error);
      res.status(400).json({ message: "Invalid MBR realtime data" });
    }
  });

  app.get("/api/sites/:id/mbr-realtime-data/latest", async (req, res) => {
    try {
      const data = await storage.getLatestMbrRealtimeData(req.params.id);
      res.json(data);
    } catch (error) {
      console.error("Error fetching latest MBR realtime data:", error);
      res.status(500).json({ message: "Failed to fetch latest MBR realtime data" });
    }
  });

  // Real-time RO Data endpoints
  app.get("/api/ro-realtime-data", async (req, res) => {
    try {
      const siteId = req.query.siteId as string;
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const data = await storage.getRoRealtimeData(siteId, limit);
      res.json(data);
    } catch (error) {
      console.error("Error fetching RO realtime data:", error);
      res.status(500).json({ message: "Failed to fetch RO realtime data" });
    }
  });

  app.post("/api/ro-realtime-data", async (req, res) => {
    try {
      const dataToInsert = insertRoRealtimeDataSchema.parse(req.body);
      const data = await storage.createRoRealtimeData(dataToInsert);
      res.status(201).json(data);
    } catch (error) {
      console.error("Error creating RO realtime data:", error);
      res.status(400).json({ message: "Invalid RO realtime data" });
    }
  });

  app.get("/api/sites/:id/ro-realtime-data/latest", async (req, res) => {
    try {
      const data = await storage.getLatestRoRealtimeData(req.params.id);
      res.json(data);
    } catch (error) {
      console.error("Error fetching latest RO realtime data:", error);
      res.status(500).json({ message: "Failed to fetch latest RO realtime data" });
    }
  });

  // SQL Viewer Endpoints
  app.get("/api/sql-viewer/databases", async (req, res) => {
    try {
      const databases = await sqlViewerService.getDatabases();
      res.json(databases);
    } catch (error) {
      console.error("Error fetching SQL databases:", error);
      res.status(500).json({ message: "Failed to fetch SQL databases" });
    }
  });

  app.get("/api/sql-viewer/databases/:database/tables", async (req, res) => {
    try {
      const tables = await sqlViewerService.getTables(req.params.database);
      res.json(tables);
    } catch (error) {
      console.error(`Error fetching tables for database ${req.params.database}:`, error);
      res.status(500).json({ message: "Failed to fetch tables" });
    }
  });

  app.get("/api/sql-viewer/databases/:database/tables/:table", async (req, res) => {
    try {
      const options = {
        limit: req.query.limit ? parseInt(req.query.limit as string) : 100,
        sortColumn: req.query.sortColumn as string,
        sortDirection: req.query.sortDirection as 'asc' | 'desc'
      };

      const data = await sqlViewerService.getTableData(req.params.database, req.params.table, options);
      res.json(data);
    } catch (error) {
      console.error(`Error fetching data for table ${req.params.table}:`, error);
      res.status(500).json({ message: "Failed to fetch table data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  sites,
  uptimeHistory,
  programBackups,
  ipcManagement,
  vfdParameters,
  alerts,
  projects,
  plcTags,
  plcTagHistory,
  siteDatabaseTags,
  siteDatabaseValues,
  mbrRealtimeData,
  roRealtimeData,
  type Site,
  type InsertSite,
  type UptimeHistory,
  type InsertUptimeHistory,
  type ProgramBackup,
  type InsertProgramBackup,
  type IpcManagement,
  type InsertIpcManagement,
  type VfdParameter,
  type InsertVfdParameter,
  type Alert,
  type InsertAlert,
  type Project,
  type InsertProject,
  type PlcTag,
  type InsertPlcTag,
  type PlcTagHistory,
  type InsertPlcTagHistory,
  type SiteDatabaseTag,
  type InsertSiteDatabaseTag,
  type SiteDatabaseValue,
  type InsertSiteDatabaseValue,
  type MbrRealtimeData,
  type InsertMbrRealtimeData,
  type RoRealtimeData,
  type InsertRoRealtimeData,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, sql, count } from "drizzle-orm";
import bcrypt from "bcrypt";

export interface IStorage {
  // Sites
  getAllSites(): Promise<Site[]>;
  getSite(id: string): Promise<Site | undefined>;
  createSite(site: InsertSite): Promise<Site>;
  updateSite(id: string, site: Partial<InsertSite>): Promise<Site | undefined>;
  deleteSite(id: string): Promise<boolean>;
  updateSiteStatus(id: string, status: string, responseTime?: number): Promise<void>;

  // Uptime History
  getUptimeHistory(siteId: string, limit?: number): Promise<UptimeHistory[]>;
  addUptimeRecord(record: InsertUptimeHistory): Promise<UptimeHistory>;
  getUptimeStats(siteId: string, hours: number): Promise<{ uptime: number; avgResponseTime: number }>;

  // Program Backups
  getProgramBackups(siteId?: string): Promise<ProgramBackup[]>;
  createProgramBackup(backup: InsertProgramBackup): Promise<ProgramBackup>;
  deleteProgramBackup(id: string): Promise<boolean>;

  // IPC Credentials
  getIpcManagement(siteId?: string): Promise<IpcManagement[]>;
  createIpcManagement(device: InsertIpcManagement): Promise<IpcManagement>;
  updateIpcManagement(id: string, device: Partial<InsertIpcManagement>): Promise<IpcManagement | undefined>;
  deleteIpcManagement(id: string): Promise<boolean>;

  // VFD Parameters
  getVfdParameters(siteId?: string): Promise<VfdParameter[]>;
  createVfdParameter(parameter: InsertVfdParameter): Promise<VfdParameter>;
  updateVfdParameter(id: string, parameter: Partial<InsertVfdParameter>): Promise<VfdParameter | undefined>;
  deleteVfdParameter(id: string): Promise<boolean>;

  // Alerts
  getAlerts(limit?: number): Promise<Alert[]>;
  createAlert(alert: InsertAlert): Promise<Alert>;
  markAlertAsRead(id: string): Promise<void>;
  markAlertAsResolved(id: string): Promise<void>;

  // PLC Tags
  getAllPlcTags(): Promise<PlcTag[]>;
  getPlcTagsBySite(siteId: string): Promise<PlcTag[]>;
  createPlcTag(tag: InsertPlcTag): Promise<PlcTag>;
  bulkCreatePlcTags(siteId: string, tags: Omit<InsertPlcTag, 'siteId'>[]): Promise<PlcTag[]>;
  updatePlcTag(id: string, tag: Partial<InsertPlcTag>): Promise<PlcTag | undefined>;
  deletePlcTag(id: string): Promise<boolean>;
  updatePlcTagValue(id: string, value: string): Promise<void>;
  createPlcTagAlert(tagId: string, value: string): Promise<void>;
  getUnreadAlertsCount(): Promise<number>;

  // PLC Tags
  getPlcTags(siteId?: string): Promise<PlcTag[]>;
  getActivePlcTags(siteId?: string): Promise<PlcTag[]>;
  createPlcTag(tag: InsertPlcTag): Promise<PlcTag>;
  updatePlcTag(id: string, tag: Partial<InsertPlcTag>): Promise<PlcTag | undefined>;
  deletePlcTag(id: string): Promise<boolean>;
  updatePlcTagValue(id: string, newValue: string, createHistory?: boolean): Promise<void>;

  // PLC Tag History
  getPlcTagHistory(tagId: string, limit?: number): Promise<PlcTagHistory[]>;
  createPlcTagHistory(history: InsertPlcTagHistory): Promise<PlcTagHistory>;

  // Projects
  getAllProjects(): Promise<Project[]>;
  getProject(id: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: string, project: Partial<InsertProject>): Promise<Project | undefined>;
  deleteProject(id: string): Promise<boolean>;

  // Dashboard metrics
  getDashboardMetrics(): Promise<{
    totalSites: number;
    onlineSites: number;
    criticalAlerts: number;
    avgResponseTime: number;
  }>;

  // Site Database Tags
  getSiteDatabaseTags(siteId?: string): Promise<SiteDatabaseTag[]>;
  getSiteDatabaseTag(id: string): Promise<SiteDatabaseTag | undefined>;
  createSiteDatabaseTag(tag: InsertSiteDatabaseTag): Promise<SiteDatabaseTag>;
  updateSiteDatabaseTag(id: string, tag: Partial<InsertSiteDatabaseTag>): Promise<SiteDatabaseTag | undefined>;
  deleteSiteDatabaseTag(id: string): Promise<boolean>;

  // Site Database Values 
  getSiteDatabaseValues(tagId: string, limit?: number): Promise<SiteDatabaseValue[]>;
  createSiteDatabaseValue(value: InsertSiteDatabaseValue): Promise<SiteDatabaseValue>;
  getLatestSiteDatabaseValues(siteId: string): Promise<Array<SiteDatabaseValue & { tag: SiteDatabaseTag }>>;

  // Real-time MBR Data
  getMbrRealtimeData(siteId?: string, limit?: number): Promise<MbrRealtimeData[]>;
  createMbrRealtimeData(data: InsertMbrRealtimeData): Promise<MbrRealtimeData>;
  getLatestMbrRealtimeData(siteId: string): Promise<MbrRealtimeData | undefined>;

  // Real-time RO Data
  getRoRealtimeData(siteId?: string, limit?: number): Promise<RoRealtimeData[]>;
  createRoRealtimeData(data: InsertRoRealtimeData): Promise<RoRealtimeData>;
  getLatestRoRealtimeData(siteId: string): Promise<RoRealtimeData | undefined>;
}

export class DatabaseStorage implements IStorage {
  // Sites
  async getAllSites(): Promise<Site[]> {
    return await db.select().from(sites).where(eq(sites.isActive, true));
  }

  async getSite(id: string): Promise<Site | undefined> {
    const [site] = await db.select().from(sites).where(eq(sites.id, id));
    return site;
  }

  async createSite(site: InsertSite): Promise<Site> {
    const [newSite] = await db.insert(sites).values(site).returning();
    return newSite;
  }

  async updateSite(id: string, site: Partial<InsertSite>): Promise<Site | undefined> {
    const [updatedSite] = await db
      .update(sites)
      .set({ ...site, updatedAt: new Date() })
      .where(eq(sites.id, id))
      .returning();
    return updatedSite;
  }

  async deleteSite(id: string): Promise<boolean> {
    const result = await db
      .update(sites)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(sites.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async updateSiteStatus(id: string, status: string, responseTime?: number): Promise<void> {
    const updateData: any = { 
      status, 
      lastCheck: new Date(),
      updatedAt: new Date()
    };

    if (responseTime !== undefined) {
      updateData.responseTime = responseTime;
    }

    if (status === "online") {
      updateData.lastOnline = new Date();
    }

    await db.update(sites).set(updateData).where(eq(sites.id, id));
  }

  // Uptime History
  async getUptimeHistory(siteId: string, limit = 100): Promise<UptimeHistory[]> {
    return await db
      .select()
      .from(uptimeHistory)
      .where(eq(uptimeHistory.siteId, siteId))
      .orderBy(desc(uptimeHistory.timestamp))
      .limit(limit);
  }

  async addUptimeRecord(record: InsertUptimeHistory): Promise<UptimeHistory> {
    const [newRecord] = await db.insert(uptimeHistory).values(record).returning();
    return newRecord;
  }

  async getUptimeStats(siteId: string, hours: number): Promise<{ uptime: number; avgResponseTime: number }> {
    const hoursAgo = new Date(Date.now() - hours * 60 * 60 * 1000);

    const result = await db
      .select({
        totalRecords: count(),
        onlineRecords: sql<number>`sum(case when ${uptimeHistory.isOnline} then 1 else 0 end)`,
        avgResponseTime: sql<number>`avg(${uptimeHistory.responseTime})`,
      })
      .from(uptimeHistory)
      .where(
        and(
          eq(uptimeHistory.siteId, siteId),
          gte(uptimeHistory.timestamp, hoursAgo)
        )
      );

    const stats = result[0];
    const uptime = stats.totalRecords > 0 ? (stats.onlineRecords / stats.totalRecords) * 100 : 0;
    const avgResponseTime = stats.avgResponseTime || 0;

    return { uptime, avgResponseTime };
  }

  // Program Backups
  async getProgramBackups(siteId?: string): Promise<ProgramBackup[]> {
    const query = db.select().from(programBackups);
    if (siteId) {
      return await query.where(eq(programBackups.siteId, siteId));
    }
    return await query;
  }

  async createProgramBackup(backup: InsertProgramBackup): Promise<ProgramBackup> {
    const [newBackup] = await db.insert(programBackups).values(backup).returning();
    return newBackup;
  }

  async deleteProgramBackup(id: string): Promise<boolean> {
    const result = await db.delete(programBackups).where(eq(programBackups.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }



  // IPC Management
  async getIpcManagement(siteId?: string): Promise<IpcManagement[]> {
    const query = db.select().from(ipcManagement);
    if (siteId) {
      return await query.where(eq(ipcManagement.siteId, siteId));
    }
    return await query;
  }

  async createIpcManagement(device: InsertIpcManagement): Promise<IpcManagement> {
    // Encrypt password before storing if provided
    let processedDevice = { ...device };
    if (device.ipcPassword) {
      const hashedPassword = await bcrypt.hash(device.ipcPassword, 10);
      processedDevice = {
        ...device,
        ipcPassword: hashedPassword,
      };
    }

    const [newDevice] = await db
      .insert(ipcManagement)
      .values(processedDevice)
      .returning();
    return newDevice;
  }

  async updateIpcManagement(id: string, device: Partial<InsertIpcManagement>): Promise<IpcManagement | undefined> {
    const updateData = { ...device, updatedAt: new Date() };

    // Encrypt password if provided
    if (device.ipcPassword) {
      updateData.ipcPassword = await bcrypt.hash(device.ipcPassword, 10);
    }

    const [updatedDevice] = await db
      .update(ipcManagement)
      .set(updateData)
      .where(eq(ipcManagement.id, id))
      .returning();
    return updatedDevice;
  }

  async deleteIpcManagement(id: string): Promise<boolean> {
    const result = await db.delete(ipcManagement).where(eq(ipcManagement.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // VFD Parameters
  async getVfdParameters(siteId?: string): Promise<VfdParameter[]> {
    if (siteId) {
      return await db
        .select()
        .from(vfdParameters)
        .where(
          and(
            eq(vfdParameters.isActive, true),
            eq(vfdParameters.siteId, siteId)
          )
        );
    }
    return await db
      .select()
      .from(vfdParameters)
      .where(eq(vfdParameters.isActive, true));
  }

  async createVfdParameter(parameter: InsertVfdParameter): Promise<VfdParameter> {
    const [newParameter] = await db.insert(vfdParameters).values(parameter).returning();
    return newParameter;
  }

  async updateVfdParameter(id: string, parameter: Partial<InsertVfdParameter>): Promise<VfdParameter | undefined> {
    const [updatedParameter] = await db
      .update(vfdParameters)
      .set({ ...parameter, updatedAt: new Date() })
      .where(eq(vfdParameters.id, id))
      .returning();
    return updatedParameter;
  }

  async deleteVfdParameter(id: string): Promise<boolean> {
    const result = await db
      .update(vfdParameters)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(vfdParameters.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }





  // Alerts
  async getAlerts(limit = 50): Promise<Alert[]> {
    return await db
      .select()
      .from(alerts)
      .orderBy(desc(alerts.createdAt))
      .limit(limit);
  }

  async createAlert(alert: InsertAlert): Promise<Alert> {
    const [newAlert] = await db.insert(alerts).values(alert).returning();
    return newAlert;
  }

  async markAlertAsRead(id: string): Promise<void> {
    await db
      .update(alerts)
      .set({ isRead: true })
      .where(eq(alerts.id, id));
  }

  async markAlertAsResolved(id: string): Promise<void> {
    await db
      .update(alerts)
      .set({ isResolved: true, resolvedAt: new Date() })
      .where(eq(alerts.id, id));
  }

  async getUnreadAlertsCount(): Promise<number> {
    const [result] = await db
      .select({ count: count() })
      .from(alerts)
      .where(eq(alerts.isRead, false));
    return result.count;
  }

  // Projects
  async getAllProjects(): Promise<Project[]> {
    return await db.select().from(projects).orderBy(desc(projects.createdAt));
  }

  async getProject(id: string): Promise<Project | undefined> {
    const [project] = await db.select().from(projects).where(eq(projects.id, id));
    return project;
  }

  async createProject(project: InsertProject): Promise<Project> {
    const [newProject] = await db.insert(projects).values(project).returning();
    return newProject;
  }

  async updateProject(id: string, project: Partial<InsertProject>): Promise<Project | undefined> {
    const [updatedProject] = await db
      .update(projects)
      .set({ ...project, updatedAt: new Date() })
      .where(eq(projects.id, id))
      .returning();
    return updatedProject;
  }

  async deleteProject(id: string): Promise<boolean> {
    const result = await db.delete(projects).where(eq(projects.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Dashboard metrics
  async getDashboardMetrics(): Promise<{
    totalSites: number;
    onlineSites: number;
    criticalAlerts: number;
    avgResponseTime: number;
  }> {
    const [siteStats] = await db
      .select({
        total: count(),
        online: count(sql`CASE WHEN ${sites.status} = 'online' THEN 1 END`),
        avgResponseTime: sql<number>`COALESCE(AVG(${sites.responseTime}), 0)`,
      })
      .from(sites)
      .where(eq(sites.isActive, true));

    const [alertStats] = await db
      .select({
        critical: count(sql`CASE WHEN ${alerts.severity} = 'critical' AND ${alerts.isResolved} = false THEN 1 END`),
      })
      .from(alerts);

    return {
      totalSites: siteStats.total,
      onlineSites: siteStats.online,
      criticalAlerts: alertStats.critical,
      avgResponseTime: Math.round(siteStats.avgResponseTime),
    };
  }

  // PLC Tags
  async getAllPlcTags(): Promise<PlcTag[]> {
    return await db.select().from(plcTags).orderBy(desc(plcTags.createdAt));
  }

  async getPlcTagsBySite(siteId: string): Promise<PlcTag[]> {
    return await db.select().from(plcTags).where(eq(plcTags.siteId, siteId)).orderBy(desc(plcTags.createdAt));
  }

  async getPlcTags(siteId?: string): Promise<PlcTag[]> {
    if (siteId) {
      return await db.select().from(plcTags).where(eq(plcTags.siteId, siteId)).orderBy(desc(plcTags.createdAt));
    }
    return await db.select().from(plcTags).orderBy(desc(plcTags.createdAt));
  }

  async getActivePlcTags(siteId?: string): Promise<PlcTag[]> {
    if (siteId) {
      return await db.select().from(plcTags).where(and(eq(plcTags.siteId, siteId), eq(plcTags.isActive, true))).orderBy(desc(plcTags.createdAt));
    }
    return await db.select().from(plcTags).where(eq(plcTags.isActive, true)).orderBy(desc(plcTags.createdAt));
  }

  async createPlcTag(tag: InsertPlcTag): Promise<PlcTag> {
    const [newTag] = await db.insert(plcTags).values(tag).returning();
    return newTag;
  }

  async bulkCreatePlcTags(siteId: string, tags: Omit<InsertPlcTag, 'siteId'>[]): Promise<PlcTag[]> {
    const tagsWithSiteId = tags.map(tag => ({ ...tag, siteId }));
    const newTags = await db.insert(plcTags).values(tagsWithSiteId).returning();
    return newTags;
  }

  async updatePlcTag(id: string, tag: Partial<InsertPlcTag>): Promise<PlcTag | undefined> {
    const [updatedTag] = await db
      .update(plcTags)
      .set({ ...tag, updatedAt: new Date() })
      .where(eq(plcTags.id, id))
      .returning();
    return updatedTag;
  }

  async deletePlcTag(id: string): Promise<boolean> {
    const result = await db.delete(plcTags).where(eq(plcTags.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async updatePlcTagValue(id: string, newValue: string, createHistory = true): Promise<void> {
    if (createHistory) {
      // Get current value first for history
      const [currentTag] = await db.select().from(plcTags).where(eq(plcTags.id, id));
      if (currentTag && currentTag.lastValue !== newValue) {
        // Create history record
        await db.insert(plcTagHistory).values({
          tagId: id,
          oldValue: currentTag.lastValue,
          newValue: newValue,
        });

        // Check if we need to create an alert for this tag change
        if (currentTag.alarmOnTrue && newValue.toLowerCase() === 'true') {
          await this.createPlcTagAlert(id, newValue);
        } else if (currentTag.alarmOnFalse && newValue.toLowerCase() === 'false') {
          await this.createPlcTagAlert(id, newValue);
        }
      }
    }

    // Update the tag value
    await db
      .update(plcTags)
      .set({ 
        lastValue: newValue, 
        lastReadTime: new Date(),
        updatedAt: new Date() 
      })
      .where(eq(plcTags.id, id));
  }

  async createPlcTagAlert(tagId: string, value: string): Promise<void> {
    // Get the tag details first
    const [tag] = await db.select().from(plcTags).where(eq(plcTags.id, tagId));
    if (!tag) return;

    // Create an alert for this PLC tag event
    await db.insert(alerts).values({
      siteId: tag.siteId,
      type: 'plc_tag_alarm',
      severity: tag.severityLevel as 'info' | 'warning' | 'critical',
      title: `${tag.tagName} Triggered`,
      message: `PLC tag ${tag.tagName} (${tag.plcAddress}) changed to ${value}`,
      isRead: false,
      isResolved: false,
    });
  }

  // PLC Tag History
  async getPlcTagHistory(tagId: string, limit = 100): Promise<PlcTagHistory[]> {
    return await db
      .select()
      .from(plcTagHistory)
      .where(eq(plcTagHistory.tagId, tagId))
      .orderBy(desc(plcTagHistory.timestamp))
      .limit(limit);
  }

  async createPlcTagHistory(history: InsertPlcTagHistory): Promise<PlcTagHistory> {
    const [newHistory] = await db.insert(plcTagHistory).values(history).returning();
    return newHistory;
  }

  // Site Database Tags
  async getSiteDatabaseTags(siteId?: string): Promise<SiteDatabaseTag[]> {
    if (siteId) {
      return await db.select().from(siteDatabaseTags).where(eq(siteDatabaseTags.siteId, siteId));
    }
    return await db.select().from(siteDatabaseTags);
  }

  async getSiteDatabaseTag(id: string): Promise<SiteDatabaseTag | undefined> {
    const [tag] = await db.select().from(siteDatabaseTags).where(eq(siteDatabaseTags.id, id));
    return tag;
  }

  async createSiteDatabaseTag(tag: InsertSiteDatabaseTag): Promise<SiteDatabaseTag> {
    const [newTag] = await db.insert(siteDatabaseTags).values(tag).returning();
    return newTag;
  }

  async updateSiteDatabaseTag(id: string, tag: Partial<InsertSiteDatabaseTag>): Promise<SiteDatabaseTag | undefined> {
    const [updatedTag] = await db
      .update(siteDatabaseTags)
      .set({ ...tag, updatedAt: new Date() })
      .where(eq(siteDatabaseTags.id, id))
      .returning();
    return updatedTag;
  }

  async deleteSiteDatabaseTag(id: string): Promise<boolean> {
    const result = await db.delete(siteDatabaseTags).where(eq(siteDatabaseTags.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Site Database Values
  async getSiteDatabaseValues(tagId: string, limit = 100): Promise<SiteDatabaseValue[]> {
    return await db
      .select()
      .from(siteDatabaseValues)
      .where(eq(siteDatabaseValues.tagId, tagId))
      .orderBy(desc(siteDatabaseValues.timestamp))
      .limit(limit);
  }

  async createSiteDatabaseValue(value: InsertSiteDatabaseValue): Promise<SiteDatabaseValue> {
    const [newValue] = await db.insert(siteDatabaseValues).values(value).returning();
    return newValue;
  }

  async getLatestSiteDatabaseValues(siteId: string): Promise<Array<SiteDatabaseValue & { tag: SiteDatabaseTag }>> {
    // Get the latest value for each tag in a site
    const result = await db
      .select({
        id: siteDatabaseValues.id,
        tagId: siteDatabaseValues.tagId,
        value: siteDatabaseValues.value,
        quality: siteDatabaseValues.quality,
        timestamp: siteDatabaseValues.timestamp,
        tag: siteDatabaseTags,
      })
      .from(siteDatabaseValues)
      .innerJoin(siteDatabaseTags, eq(siteDatabaseValues.tagId, siteDatabaseTags.id))
      .where(eq(siteDatabaseTags.siteId, siteId))
      .orderBy(desc(siteDatabaseValues.timestamp));

    // Filter to get only the latest value for each tag
    const latestValues = new Map();
    result.forEach(row => {
      if (!latestValues.has(row.tagId) || latestValues.get(row.tagId).timestamp < row.timestamp) {
        latestValues.set(row.tagId, row);
      }
    });

    return Array.from(latestValues.values());
  }

  // Real-time MBR Data methods
  async getMbrRealtimeData(siteId?: string, limit = 100): Promise<MbrRealtimeData[]> {
    const query = db.select().from(mbrRealtimeData);

    if (siteId) {
      return await query
        .where(eq(mbrRealtimeData.siteId, siteId))
        .orderBy(desc(mbrRealtimeData.timestamp))
        .limit(limit);
    }

    return await query
      .orderBy(desc(mbrRealtimeData.timestamp))
      .limit(limit);
  }

  async createMbrRealtimeData(data: InsertMbrRealtimeData): Promise<MbrRealtimeData> {
    const [newData] = await db.insert(mbrRealtimeData).values(data).returning();
    return newData;
  }

  async getLatestMbrRealtimeData(siteId: string): Promise<MbrRealtimeData | undefined> {
    const [latest] = await db
      .select()
      .from(mbrRealtimeData)
      .where(eq(mbrRealtimeData.siteId, siteId))
      .orderBy(desc(mbrRealtimeData.timestamp))
      .limit(1);
    return latest;
  }

  // Real-time RO Data methods
  async getRoRealtimeData(siteId?: string, limit = 100): Promise<RoRealtimeData[]> {
    const query = db.select().from(roRealtimeData);

    if (siteId) {
      return await query
        .where(eq(roRealtimeData.siteId, siteId))
        .orderBy(desc(roRealtimeData.timestamp))
        .limit(limit);
    }

    return await query
      .orderBy(desc(roRealtimeData.timestamp))
      .limit(limit);
  }

  async createRoRealtimeData(data: InsertRoRealtimeData): Promise<RoRealtimeData> {
    const [newData] = await db.insert(roRealtimeData).values(data).returning();
    return newData;
  }

  async getLatestRoRealtimeData(siteId: string): Promise<RoRealtimeData | undefined> {
    const [latest] = await db
      .select()
      .from(roRealtimeData)
      .where(eq(roRealtimeData.siteId, siteId))
      .orderBy(desc(roRealtimeData.timestamp))
      .limit(1);
    return latest;
  }
}

export const storage = new DatabaseStorage();

