              <div className="space-y-3">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="h-16 bg-gray-100 rounded animate-pulse" />
                ))}
              </div>
            ) : vfdParameters && vfdParameters.length > 0 ? (
              vfdParameters.slice(0, 3).map((vfd) => (
                <div
                  key={vfd.id}
