import { spawn } from 'child_process';
import path from 'path';
import { fileURLToPath } from 'url';
import { storage } from '../storage';
import type { Site, SiteDatabaseTag } from '@shared/schema';

// Get the directory name in an ES module context
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Alias Site as ADSConnection for consistency within this service
type ADSConnection = Site;

class ADSMonitoringService {
  private static instance: ADSMonitoringService;
  private monitoringIntervals: Map<string, NodeJS.Timeout> = new Map();

  private constructor() {}

  public static getInstance(): ADSMonitoringService {
    if (!ADSMonitoringService.instance) {
      ADSMonitoringService.instance = new ADSMonitoringService();
    }
    return ADSMonitoringService.instance;
  }

  /**
   * Executes the Python script to read a tag value from the PLC.
   * This is the core function that replaces the direct node-ads connection.
   */
  private readADSTag(tag: SiteDatabaseTag): Promise<number> {
    return new Promise((resolve, reject) => {
      // Path to the Python script, using the correct __dirname for ES modules
      const scriptPath = path.join(__dirname, '..', '..', 'ads.py');
      const pythonProcess = spawn('python', [scriptPath, tag.adsPath]);

      let dataString = '';
      let errorString = '';

      // Capture standard output from the script
      pythonProcess.stdout.on('data', (data) => {
        dataString += data.toString();
      });

      // Capture standard error from the script
      pythonProcess.stderr.on('data', (data) => {
        errorString += data.toString();
      });

      // Handle the script exit
      pythonProcess.on('close', (code) => {
        if (code !== 0) {
          console.error(`[ADS] Python script exited with code ${code}: ${errorString}`);
          return reject(new Error(`Python script error: ${errorString.trim()}`));
        }

        const value = parseFloat(dataString.trim());
        if (isNaN(value)) {
          return reject(new Error('Failed to parse float value from Python script.'));
        }

        console.log(`[ADS] Successfully read tag '${tag.adsPath}', value: ${value}`);
        resolve(value);
      });

      pythonProcess.on('error', (err) => {
        console.error('[ADS] Failed to start Python script.', err);
        reject(err);
      });
    });
  }

  /**
   * Public method to test the connection by reading a single tag.
   * The 'connection' parameter is kept for API compatibility but is no longer used.
   */
  public async testADSConnection(connection: ADSConnection, tag: SiteDatabaseTag): Promise<any> {
    console.log(`[ADS] Testing connection for site ${connection.id} by reading tag ${tag.adsPath}`);
    try {
      const value = await this.readADSTag(tag);
      return { success: true, value };
    } catch (error: any) {
      console.error(`[ADS] Test connection failed for site ${connection.id}:`, error.message);
      return { success: false, error: error.message };
    }
  }

  public startMonitoringForTag(tag: SiteDatabaseTag) {
    // Ensure the tag is active and not already being monitored
    if (tag.isActive && !this.monitoringIntervals.has(tag.id)) {
      console.log(`[ADS] Starting monitoring for new tag: ${tag.tagName} (${tag.adsPath})`);

      const interval = setInterval(async () => {
        try {
          const value = await this.readADSTag(tag);
          // Save the new value to the database
          await storage.createSiteDatabaseValue({
            tagId: tag.id,
            value: value.toString(),
          });
        } catch (error) {
          console.error(`[ADS] Error monitoring tag ${tag.tagName}:`, error);
        }
      }, tag.scanInterval || 5000); // Use tag's interval or default to 5s

      this.monitoringIntervals.set(tag.id, interval);
    }
  }

  public startMonitoringAllSites(connections: ADSConnection[], tags: SiteDatabaseTag[]) {
    console.log(`[ADS] Starting monitoring for ${tags.length} tags across ${connections.length} sites.`);

    for (const tag of tags) {
      this.startMonitoringForTag(tag);
    }
  }

  public stopMonitoringForSite(siteId: string) {
    // This would need to be adapted to stop monitoring by site, e.g. by finding all tags for that site
    console.log(`[ADS] Stopping monitoring for site ${siteId}...`);
    for (const [tagId, interval] of this.monitoringIntervals.entries()) {
      // This is a simplified stop, a real implementation would need to map siteId to tagIds
      clearInterval(interval);
      this.monitoringIntervals.delete(tagId);
    }
  }
}

export const adsMonitoringService = ADSMonitoringService.getInstance();

client/src/pages/plc-tags-new.tsx.content:
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { PlcTag, InsertPlcTag } from "@shared/schema";
import {
  Activity,
  Upload,
  Edit,
  Save,
  X,
  Trash2,
  AlertTriangle,
  CheckCircle,
  Clock,
} from "lucide-react";

export default function PlcTagsPage() {
  const { toast } = useToast();
  const [selectedSiteId, setSelectedSiteId] = useState<string>("");
  const [editingTagId, setEditingTagId] = useState<string | null>(null);
  const [editFormData, setEditFormData] = useState<Partial<PlcTag>>({});
  const [showNewTagForm, setShowNewTagForm] = useState(false);
  const [newTagData, setNewTagData] = useState<Partial<InsertPlcTag>>({});
  const [showBulkUpload, setShowBulkUpload] = useState(false);
  const [bulkUploadData, setBulkUploadData] = useState<string>("");

  // Fetch sites for selection
  const { data: sites = [] } = useQuery<any[]>({
    queryKey: ["/api/sites"],
  });

  // Fetch PLC tags for selected site
  const { data: plcTags = [], isLoading, refetch } = useQuery<PlcTag[]>({
    queryKey: ["/api/plc-tags", selectedSiteId],
    queryFn: () => fetch(`/api/plc-tags${selectedSiteId ? `?siteId=${selectedSiteId}` : ""}`).then(res => res.json()),
  });

  // Create PLC tag mutation
  const createTagMutation = useMutation({
    mutationFn: (data: InsertPlcTag) => 
      apiRequest("/api/plc-tags", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      setShowNewTagForm(false);
      setNewTagData({});
      toast({
        title: "Success",
        description: "PLC tag created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to create PLC tag",
        variant: "destructive",
      });
    },
  });

  // Update PLC tag mutation
  const updateTagMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertPlcTag> }) =>
      apiRequest(`/api/plc-tags/${id}`, "PUT", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      setEditingTagId(null);
      setEditFormData({});
      toast({
        title: "Success",
        description: "PLC tag updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error", 
        description: error?.message || "Failed to update PLC tag",
        variant: "destructive",
      });
    },
  });

  // Delete PLC tag mutation
  const deleteTagMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/plc-tags/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      toast({
        title: "Success",
        description: "PLC tag deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to delete PLC tag",
        variant: "destructive",
      });
    },
  });

  // Bulk upload mutation
  const bulkUploadMutation = useMutation({
    mutationFn: async (csvData: string) => {
      const lines = csvData.trim().split('\n');
      const headers = lines[0].split(',').map(h => h.trim());

      if (!headers.includes('tagName') || !headers.includes('plcAddress')) {
        throw new Error("CSV must contain 'tagName' and 'plcAddress' columns");
      }

      const tags: InsertPlcTag[] = [];
      for (let i = 1; i < lines.length; i++) {
        const values = lines[i].split(',').map(v => v.trim());
        const tagData: any = { siteId: selectedSiteId };

        headers.forEach((header, index) => {
          if (values[index]) {
            switch (header) {
              case 'isActive':
              case 'alarmOnTrue':
              case 'alarmOnFalse':
                tagData[header] = values[index].toLowerCase() === 'true';
                break;
              default:
                tagData[header] = values[index];
            }
          }
        });

        if (tagData.tagName && tagData.plcAddress) {
          tags.push({
            ...tagData,
            dataType: tagData.dataType || 'BOOL',
            severityLevel: tagData.severityLevel || 'warning',
            isActive: tagData.isActive ?? true,
            alarmOnTrue: tagData.alarmOnTrue ?? false,
            alarmOnFalse: tagData.alarmOnFalse ?? false,
          });
        }
      }

      // Create all tags
      for (const tag of tags) {
        await apiRequest("/api/plc-tags", "POST", tag);
      }

      return { count: tags.length };
    },
    onSuccess: (result) => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      setShowBulkUpload(false);
      setBulkUploadData("");
      toast({
        title: "Success",
        description: `${result.count} PLC tags uploaded successfully`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to upload PLC tags",
        variant: "destructive",
      });
    },
  });

  const dataTypeOptions = ["BOOL", "INT", "REAL", "STRING"];
  const severityOptions = ["info", "warning", "critical"];

  const handleEditStart = (tag: PlcTag) => {
    setEditingTagId(tag.id);
    setEditFormData({ ...tag });
  };

  const handleEditCancel = () => {
    setEditingTagId(null);
    setEditFormData({});
  };

  const handleEditSave = () => {
    if (editingTagId && editFormData) {
      updateTagMutation.mutate({ id: editingTagId, data: editFormData });
    }
  };

  const handleFieldChange = (field: keyof PlcTag, value: any) => {
    setEditFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleNewTag = () => {
    setShowNewTagForm(true);
    setNewTagData({ siteId: selectedSiteId });
  };

  const handleNewTagSave = () => {
    if (newTagData.tagName && newTagData.plcAddress && selectedSiteId) {
      createTagMutation.mutate({
        ...newTagData,
        siteId: selectedSiteId,
        dataType: newTagData.dataType || 'BOOL',
        severityLevel: newTagData.severityLevel || 'warning',
        isActive: newTagData.isActive ?? true,
        alarmOnTrue: newTagData.alarmOnTrue ?? false,
        alarmOnFalse: newTagData.alarmOnFalse ?? false,
      } as InsertPlcTag);
    } else {
      toast({
        title: "Validation Error",
        description: "Tag Name, PLC Address and Site selection are required",
        variant: "destructive",
      });
    }
  };

  const handleNewTagCancel = () => {
    setShowNewTagForm(false);
    setNewTagData({});
  };

  const handleNewTagChange = (field: keyof InsertPlcTag, value: any) => {
    setNewTagData((prev) => ({ ...prev, [field]: value }));
  };

  const handleDelete = (id: string) => {
    if (window.confirm("Are you sure you want to delete this PLC tag?")) {
      deleteTagMutation.mutate(id);
    }
  };

  const handleBulkUpload = () => {
    if (!selectedSiteId) {
      toast({
        title: "Site Required",
        description: "Please select a site first",
        variant: "destructive",
      });
      return;
    }
    if (!bulkUploadData.trim()) {
      toast({
        title: "Data Required",
        description: "Please enter CSV data",
        variant: "destructive",
      });
      return;
    }
    bulkUploadMutation.mutate(bulkUploadData);
  };

  const getStatusColor = (isActive: boolean | null | undefined) => {
    return isActive ? "bg-green-500" : "bg-gray-500";
  };

  const getSeverityColor = (severity: string | null | undefined) => {
    switch (severity) {
      case "critical":
        return "bg-red-500";
      case "warning":
        return "bg-yellow-500";
      case "info":
        return "bg-blue-500";
      default:
        return "bg-gray-500";
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <Header
        title="PLC Tag Monitoring"
        subtitle="Manage Beckhoff PLC tags and real-time monitoring configurations"
      />

      <div className="p-6">
        {/* Site Selection */}
        <div className="mb-6">
          <Label className="text-sm font-semibold text-gray-700 mb-2 block">Select Site</Label>
          <div className="w-full max-w-md">
            <Select value={selectedSiteId} onValueChange={setSelectedSiteId}>
              <SelectTrigger className="text-sm h-9 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all">
                <SelectValue placeholder="Choose a site to manage PLC tags" />
              </SelectTrigger>
              <SelectContent>
                {sites.map((site: any) => (
                  <SelectItem key={site.id} value={site.id}>
                    {site.name} ({site.location})
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>

        {selectedSiteId && (
          <>
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-lg font-semibold text-gray-900">
                PLC Tags ({plcTags.length})
              </h3>
              <div className="flex gap-3">
                <Button 
                  onClick={() => setShowBulkUpload(true)} 
                  variant="outline"
                  className="bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white font-medium transition-all duration-200 shadow-md hover:shadow-lg px-6 py-2"
                >
                  <Upload size={16} className="mr-2" />
                  Bulk Upload
                </Button>
                <Button 
                  onClick={handleNewTag} 
                  className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white font-medium transition-all duration-200 shadow-md hover:shadow-lg px-6 py-2"
                >
                  <Activity size={16} className="mr-2" />
                  Add PLC Tag
                </Button>
              </div>
            </div>

            {/* New Tag Form */}
            {showNewTagForm && (
              <Card className="mb-6 border-2 border-blue-200 shadow-lg bg-gradient-to-br from-blue-50/30 to-white">
                <CardHeader className="bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-t-lg">
                  <CardTitle className="text-lg font-semibold flex items-center gap-2">
                    <Activity size={20} />
                    Add New PLC Tag
                  </CardTitle>
                </CardHeader>
                <CardContent className="p-6">
                  <div className="space-y-8">
                    <div className="bg-white rounded-lg border border-gray-100 p-4 shadow-sm">
                      <h4 className="text-sm font-semibold text-gray-800 mb-4 border-b border-gray-200 pb-2 flex items-center gap-2">
                        <Activity size={16} className="text-blue-600" />
                        Basic Details
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
                        <div>
                          <Label className="text-xs font-semibold text-gray-700">Tag Name</Label>
                          <Input
                            value={newTagData.tagName || ""}
                            onChange={(e) => handleNewTagChange("tagName", e.target.value)}
                            className="text-sm h-9 mt-1 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                            placeholder="Enter tag name"
                          />
                        </div>
                        <div>
                          <Label className="text-xs font-semibold text-gray-700">PLC Address</Label>
                          <Input
                            value={newTagData.plcAddress || ""}
                            onChange={(e) => handleNewTagChange("plcAddress", e.target.value)}
                            className="text-sm h-9 mt-1 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                            placeholder="e.g., GVL.M_PUMP_TRIP"
                          />
                        </div>
                        <div>
                          <Label className="text-xs font-semibold text-gray-700">Data Type</Label>
                          <Select
                            value={newTagData.dataType || "BOOL"}
                            onValueChange={(value) => handleNewTagChange("dataType", value)}
                          >
                            <SelectTrigger className="text-sm h-9 mt-1 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              {dataTypeOptions.map((type) => (
                                <SelectItem key={type} value={type}>{type}</SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>
                        <div>
                          <Label className="text-xs font-semibold text-gray-700">Severity</Label>
                          <Select
                            value={newTagData.severityLevel || "warning"}
                            onValueChange={(value) => handleNewTagChange("severityLevel", value)}
                          >
                            <SelectTrigger className="text-sm h-9 mt-1 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              {severityOptions.map((severity) => (
                                <SelectItem key={severity} value={severity}>{severity}</SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>
                        <div className="flex items-end">
                          <div className="flex items-center space-x-2">
                            <input
                              type="checkbox"
                              id="newActive"
                              checked={newTagData.isActive ?? true}
                              onChange={(e) => handleNewTagChange("isActive", e.target.checked)}
                              className="rounded border-gray-300"
                            />
                            <Label htmlFor="newActive" className="text-xs font-semibold text-gray-700">Active</Label>
                          </div>
                        </div>
                        <div className="flex items-end">
                          <div className="flex items-center space-x-2">
                            <input
                              type="checkbox"
                              id="newAlarmTrue"
                              checked={newTagData.alarmOnTrue ?? false}
                              onChange={(e) => handleNewTagChange("alarmOnTrue", e.target.checked)}
                              className="rounded border-gray-300"
                            />
                            <Label htmlFor="newAlarmTrue" className="text-xs font-semibold text-gray-700">Alarm on TRUE</Label>
                          </div>
                        </div>
                      </div>
                      <div className="mt-4">
                        <Label className="text-xs font-semibold text-gray-700">Description</Label>
                        <Textarea
                          value={newTagData.description || ""}
                          onChange={(e) => handleNewTagChange("description", e.target.value)}
                          className="text-sm mt-1 border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                          placeholder="Optional description"
                          rows={2}
                        />
                      </div>
                    </div>
                  </div>

                  <div className="flex justify-end gap-3 pt-6 border-t border-gray-200">
                    <Button 
                      variant="outline" 
                      onClick={handleNewTagCancel}
                      className="px-6 py-2 text-sm font-medium"
                    >
                      <X size={16} className="mr-2" />
                      Cancel
                    </Button>
                    <Button 
                      onClick={handleNewTagSave}
                      disabled={createTagMutation.isPending}
                      className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white font-medium px-6 py-2 text-sm"
                    >
                      <Save size={16} className="mr-2" />
                      {createTagMutation.isPending ? "Creating..." : "Create Tag"}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Bulk Upload Form */}
            {showBulkUpload && (
              <Card className="mb-6 border-2 border-green-200 shadow-lg bg-gradient-to-br from-green-50/30 to-white">
                <CardHeader className="bg-gradient-to-r from-green-600 to-green-700 text-white rounded-t-lg">
                  <CardTitle className="text-lg font-semibold flex items-center gap-2">
                    <Upload size={20} />
                    Bulk Upload PLC Tags
                  </CardTitle>
                </CardHeader>
                <CardContent className="p-6">
                  <div className="space-y-4">
                    <div>
                      <Label className="text-sm font-semibold text-gray-700 mb-2 block">CSV Format</Label>
                      <p className="text-xs text-gray-600 mb-2">
                        Required columns: tagName, plcAddress. Optional: dataType, severityLevel, description, isActive, alarmOnTrue, alarmOnFalse
                      </p>
                      <div className="bg-gray-50 p-3 rounded text-xs font-mono text-gray-700">
                        tagName,plcAddress,dataType,severityLevel,description,isActive,alarmOnTrue<br />
                        Pump Trip,GVL.M_PUMP_TRIP,BOOL,critical,Main pump trip alarm,true,true<br />
                        Level Sensor,GVL.LEVEL_HIGH,BOOL,warning,High level sensor,true,true
                      </div>
                    </div>
                    <div>
                      <Label className="text-sm font-semibold text-gray-700">CSV Data</Label>
                      <Textarea
                        value={bulkUploadData}
                        onChange={(e) => setBulkUploadData(e.target.value)}
                        className="mt-1 text-sm font-mono"
                        placeholder="Paste your CSV data here..."
                        rows={8}
                      />
                    </div>
                  </div>

                  <div className="flex justify-end gap-3 pt-6 border-t border-gray-200">
                    <Button 
                      variant="outline" 
                      onClick={() => setShowBulkUpload(false)}
                      className="px-6 py-2 text-sm font-medium"
                    >
                      <X size={16} className="mr-2" />
                      Cancel
                    </Button>
                    <Button 
                      onClick={handleBulkUpload}
                      disabled={bulkUploadMutation.isPending}
                      className="bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white font-medium px-6 py-2 text-sm"
                    >
                      <Upload size={16} className="mr-2" />
                      {bulkUploadMutation.isPending ? "Uploading..." : "Upload Tags"}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* PLC Tags Table */}
            <div className="space-y-4">
              {isLoading ? (
                <div className="text-center py-8 text-gray-500">Loading PLC tags...</div>
              ) : plcTags.length === 0 ? (
                <div className="text-center py-12 text-gray-500">
                  <Activity className="w-16 h-16 mx-auto mb-4 opacity-30" />
                  <h3 className="text-lg font-semibold mb-2">No PLC Tags</h3>
                  <p className="text-sm">No PLC tags configured for this site. Add your first tag to start monitoring.</p>
                </div>
              ) : (
                plcTags.map((tag: PlcTag) => (
                  <Card key={tag.id} className="border border-gray-200 shadow-sm hover:shadow-md transition-shadow">
                    <CardContent className="p-4">
                      {editingTagId === tag.id ? (
                        // Edit Mode
                        <div className="space-y-4">
                          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
                            <div>
                              <Label className="text-xs font-semibold text-gray-700">Tag Name</Label>
                              <Input
                                value={editFormData.tagName || ""}
                                onChange={(e) => handleFieldChange("tagName", e.target.value)}
                                className="text-sm h-9 mt-1"
                              />
                            </div>
                            <div>
                              <Label className="text-xs font-semibold text-gray-700">PLC Address</Label>
                              <Input
                                value={editFormData.plcAddress || ""}
                                onChange={(e) => handleFieldChange("plcAddress", e.target.value)}
                                className="text-sm h-9 mt-1"
                              />
                            </div>
                            <div>
                              <Label className="text-xs font-semibold text-gray-700">Data Type</Label>
                              <Select
                                value={editFormData.dataType || "BOOL"}
                                onValueChange={(value) => handleFieldChange("dataType", value)}
                              >
                                <SelectTrigger className="text-sm h-9 mt-1">
                                  <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                  {dataTypeOptions.map((type) => (
                                    <SelectItem key={type} value={type}>{type}</SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                            </div>
                            <div>
                              <Label className="text-xs font-semibold text-gray-700">Severity</Label>
                              <Select
                                value={editFormData.severityLevel || "warning"}
                                onValueChange={(value) => handleFieldChange("severityLevel", value)}
                              >
                                <SelectTrigger className="text-sm h-9 mt-1">
                                  <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                  {severityOptions.map((severity) => (
                                    <SelectItem key={severity} value={severity}>{severity}</SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                            </div>
                            <div className="flex items-end">
                              <div className="flex items-center space-x-2">
                                <input
                                  type="checkbox"
                                  id={`active-${tag.id}`}
                                  checked={editFormData.isActive ?? true}
                                  onChange={(e) => handleFieldChange("isActive", e.target.checked)}
                                  className="rounded border-gray-300"
                                />
                                <Label htmlFor={`active-${tag.id}`} className="text-xs font-semibold text-gray-700">Active</Label>
                              </div>
                            </div>
                            <div className="flex items-end">
                              <div className="flex items-center space-x-2">
                                <input
                                  type="checkbox"
                                  id={`alarm-${tag.id}`}
                                  checked={editFormData.alarmOnTrue ?? false}
                                  onChange={(e) => handleFieldChange("alarmOnTrue", e.target.checked)}
                                  className="rounded border-gray-300"
                                />
                                <Label htmlFor={`alarm-${tag.id}`} className="text-xs font-semibold text-gray-700">Alarm on TRUE</Label>
                              </div>
                            </div>
                          </div>
                          <div>
                            <Label className="text-xs font-semibold text-gray-700">Description</Label>
                            <Textarea
                              value={editFormData.description || ""}
                              onChange={(e) => handleFieldChange("description", e.target.value)}
                              className="text-sm mt-1"
                              rows={2}
                            />
                          </div>
                          <div className="flex justify-end gap-2">
                            <Button variant="outline" size="sm" onClick={handleEditCancel}>
                              <X size={16} className="mr-1" />
                              Cancel
                            </Button>
                            <Button size="sm" onClick={handleEditSave} disabled={updateTagMutation.isPending}>
                              <Save size={16} className="mr-1" />
                              Save
                            </Button>
                          </div>
                        </div>
                      ) : (
                        // View Mode
                        <div className="space-y-3">
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-4">
                              <div>
                                <h3 className="text-sm font-semibold text-gray-900">{tag.tagName}</h3>
                                <p className="text-xs text-gray-600 font-mono">{tag.plcAddress}</p>
                              </div>
                              <div className="flex items-center space-x-2">
                                <div className={`w-2 h-2 rounded-full ${getStatusColor(tag.isActive)}`}></div>
                                <Badge variant="outline" className="text-xs">
                                  {tag.dataType}
                                </Badge>
                                <div className={`w-2 h-2 rounded-full ${getSeverityColor(tag.severityLevel)}`}></div>
                                <span className="text-xs text-gray-600 capitalize">{tag.severityLevel}</span>
                              </div>
                            </div>
                            <div className="flex items-center space-x-2">
                              <Button variant="outline" size="sm" onClick={() => handleEditStart(tag)}>
                                <Edit size={14} />
                              </Button>
                              <Button variant="outline" size="sm" onClick={() => handleDelete(tag.id)}>
                                <Trash2 size={14} />
                              </Button>
                            </div>
                          </div>

                          {tag.description && (
                            <p className="text-xs text-gray-600">{tag.description}</p>
                          )}

                          <div className="flex items-center justify-between text-xs text-gray-500">
                            <div className="flex items-center space-x-4">
                              <div className="flex items-center space-x-1">
                                <span>Value:</span>
                                <Badge variant="outline" className="text-xs">
                                  {tag.lastValue || "No data"}
                                </Badge>
                              </div>
                              {tag.alarmOnTrue && (
                                <div className="flex items-center space-x-1">
                                  <AlertTriangle size={12} className="text-orange-500" />
                                  <span>Alarm on TRUE</span>
                                </div>
                              )}
                              {tag.alarmOnFalse && (
                                <div className="flex items-center space-x-1">
                                  <CheckCircle size={12} className="text-blue-500" />
                                  <span>Alarm on FALSE</span>
                                </div>
                              )}
                            </div>
                            {tag.lastReadTime && (
                              <div className="flex items-center space-x-1">
                                <Clock size={12} />
                                <span>Last read: {new Date(tag.lastReadTime).toLocaleString()}</span>
                              </div>
                            )}
                          </div>
                        </div>
                      )}
                    </CardContent>
                  </Card>
                ))
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

client/src/pages/site-database.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { toast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Plus, 
  Database,
  Edit,
  Trash2,
  CheckCircle,
  XCircle,
  Activity,
  Gauge
} from "lucide-react";
import { insertSiteDatabaseTagSchema } from "@shared/schema";
import type { 
  Site, 
  SiteDatabaseTag, 
  SiteDatabaseValue
} from "@shared/schema";
import { z } from "zod";
import { format } from "date-fns";

const tagFormSchema = insertSiteDatabaseTagSchema;

// Utility functions
const formatTimestamp = (timestamp: string | Date) => {
  const date = typeof timestamp === "string" ? new Date(timestamp) : timestamp;
  return format(date, "MMM dd, yyyy HH:mm:ss");
};

type TagFormValues = z.infer<typeof tagFormSchema>;

export default function SiteDatabase() {
  const [selectedSite, setSelectedSite] = useState<string>("");
  const [isTagDialogOpen, setIsTagDialogOpen] = useState(false);
  const [editingTag, setEditingTag] = useState<SiteDatabaseTag | null>(null);
  const queryClient = useQueryClient();

  // Fetch sites
  const { data: sites = [] } = useQuery<Site[]>({
    queryKey: ["/api/sites"],
  });

  // Fetch tags for selected site
  const { data: tags = [], isLoading: tagsLoading } = useQuery({
    queryKey: ["/api/site-database-tags", selectedSite],
    queryFn: async () => {
      const response = await fetch(`/api/site-database-tags?siteId=${selectedSite}`);
      return response.json() as Promise<SiteDatabaseTag[]>;
    },
    enabled: !!selectedSite,
  });

  // Fetch latest values for selected site
  const { data: latestValues = [] } = useQuery({
    queryKey: ["/api/sites", selectedSite, "database-values", "latest"],
    queryFn: async () => {
      const response = await fetch(`/api/sites/${selectedSite}/database-values/latest`);
      return response.json() as Promise<Array<SiteDatabaseValue & { tag: SiteDatabaseTag }>>;
    },
    enabled: !!selectedSite,
    refetchInterval: 3000, // Refresh every 3 seconds
  });

  // Tag form
  const form = useForm<TagFormValues>({
    resolver: zodResolver(tagFormSchema),
    defaultValues: {
      siteId: "",
      tagName: "",
      adsPath: "",
      dataType: "INT",
      description: "",
      scanInterval: 2000,
      isActive: true,
    },
  });

  // Create/Update tag mutation
  const createTagMutation = useMutation({
    mutationFn: async (data: TagFormValues) => {
      if (editingTag) {
        return apiRequest(`/api/site-database-tags/${editingTag.id}`, "PUT", data);
      }
      return apiRequest("/api/site-database-tags", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/site-database-tags"] });
      setIsTagDialogOpen(false);
      setEditingTag(null);
      form.reset();
      toast({
        title: editingTag ? "Tag updated" : "Tag created",
        description: `ADS tag has been ${editingTag ? "updated" : "created"} successfully.`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: `Failed to ${editingTag ? "update" : "create"} tag: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  // Delete tag mutation
  const deleteTagMutation = useMutation({
    mutationFn: (tagId: string) =>
      apiRequest(`/api/site-database-tags/${tagId}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/site-database-tags"] });
      toast({
        title: "Tag deleted",
        description: "ADS tag has been deleted successfully.",
      });
    },
  });

  const onSubmit = (data: TagFormValues) => {
    createTagMutation.mutate({ ...data, siteId: selectedSite });
  };

  const handleEditTag = (tag: SiteDatabaseTag) => {
    setEditingTag(tag);
    form.reset({
      siteId: tag.siteId,
      tagName: tag.tagName,
      adsPath: tag.adsPath,
      dataType: tag.dataType,
      description: tag.description || "",
      scanInterval: tag.scanInterval || 2000,
      isActive: tag.isActive,
    });
    setIsTagDialogOpen(true);
  };

  const handleDeleteTag = (tagId: string) => {
    if (confirm("Are you sure you want to delete this tag?")) {
      deleteTagMutation.mutate(tagId);
    }
  };

  const handleAddTag = () => {
    setEditingTag(null);
    form.reset({
      siteId: selectedSite,
      tagName: "",
      adsPath: "",
      dataType: "INT",
      description: "",
      scanInterval: 2000,
      isActive: true,
    });
    setIsTagDialogOpen(true);
  };

  const getDataTypeIcon = (dataType: string) => {
    switch (dataType.toUpperCase()) {
      case 'BOOL':
        return <CheckCircle className="h-4 w-4" />;
      case 'INT':
      case 'DINT':
        return <Gauge className="h-4 w-4" />;
      case 'REAL':
        return <Activity className="h-4 w-4" />;
      case 'STRING':
        return <Database className="h-4 w-4" />;
      default:
        return <Database className="h-4 w-4" />;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6" data-testid="site-database-page">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight" data-testid="page-title">Site Database</h1>
          <p className="text-muted-foreground" data-testid="page-description">
            Manage ADS tags and monitor real-time data collection from industrial sites
          </p>
        </div>
      </div>

      {/* Site Selection */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center gap-4">
            <div className="flex-1">
              <Select value={selectedSite} onValueChange={setSelectedSite} data-testid="select-site">
                <SelectTrigger>
                  <SelectValue placeholder="Select a site..." />
                </SelectTrigger>
                <SelectContent>
                  {sites.map((site) => (
                    <SelectItem key={site.id} value={site.id}>
                      {site.name} ({site.ipAddress})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            {selectedSite && (
              <div className="flex items-center gap-2">
                {sites.find(s => s.id === selectedSite)?.status === 'online' ? (
                  <Badge className="bg-green-500">Online</Badge>
                ) : (
                  <Badge variant="destructive">Offline</Badge>
                )}
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* ADS Tags Section */}
      {selectedSite && (
        <Card>
          <CardHeader className="pb-4">
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <Database className="h-5 w-5" />
                  ADS Tags Configuration
                </CardTitle>
                <CardDescription>
                  Configure ADS tags for real-time data collection
                </CardDescription>
              </div>
              <Button onClick={handleAddTag} data-testid="button-add-tag">
                <Plus className="h-4 w-4 mr-2" />
                Add Tag
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {tagsLoading ? (
              <div className="text-center py-8">Loading tags...</div>
            ) : tags.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No ADS tags configured for this site
              </div>
            ) : (
              <div className="rounded-md border">
                <Table>
                  <TableHeader>
                    <TableRow className="bg-muted/50">
                      <TableHead className="font-semibold">Tag Name</TableHead>
                      <TableHead className="font-semibold">ADS Path</TableHead>
                      <TableHead className="font-semibold">Data Type</TableHead>
                      <TableHead className="font-semibold">Current Value</TableHead>
                      <TableHead className="font-semibold">Status</TableHead>
                      <TableHead className="font-semibold w-24">Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {tags.map((tag, index) => {
                      const latestValue = latestValues.find(v => v.tagId === tag.id);
                      return (
                        <TableRow key={tag.id} data-testid={`tag-row-${tag.id}`} className={index % 2 === 0 ? "bg-muted/25" : ""}>
                          <TableCell className="font-medium">
                            <div className="flex items-center gap-2">
                              {getDataTypeIcon(tag.dataType)}
                              {tag.tagName}
                            </div>
                          </TableCell>
                          <TableCell className="font-mono text-sm">{tag.adsPath}</TableCell>
                          <TableCell>
                            <Badge variant="outline">{tag.dataType}</Badge>
                          </TableCell>
                          <TableCell className="font-mono font-semibold text-blue-600">
                            {latestValue ? latestValue.value : "-"}
                          </TableCell>
                          <TableCell>
                            {tag.isActive ? (
                              <Badge className="bg-green-500">Active</Badge>
                            ) : (
                              <Badge variant="secondary">Inactive</Badge>
                            )}
                          </TableCell>
                          <TableCell>
                            <div className="flex items-center gap-1">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleEditTag(tag)}
                                data-testid={`button-edit-${tag.id}`}
                              >
                                <Edit className="h-4 w-4" />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleDeleteTag(tag.id)}
                                data-testid={`button-delete-${tag.id}`}
                              >
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </div>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Add/Edit Tag Dialog */}
      <Dialog open={isTagDialogOpen} onOpenChange={setIsTagDialogOpen}>
        <DialogContent className="max-w-md" data-testid="tag-dialog">
          <DialogHeader>
            <DialogTitle>{editingTag ? "Edit ADS Tag" : "Add ADS Tag"}</DialogTitle>
            <DialogDescription>
              {editingTag ? "Update the ADS tag configuration" : "Configure a new ADS tag for data collection"}
            </DialogDescription>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="tagName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Tag Name</FormLabel>
                    <FormControl>
                      <Input placeholder="e.g., Temperature_01" {...field} data-testid="input-tag-name" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="adsPath"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>ADS Path</FormLabel>
                    <FormControl>
                      <Input placeholder="e.g., GVL.Temperature" {...field} data-testid="input-ads-path" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="dataType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Data Type</FormLabel>
                    <FormControl>
                      <Select value={field.value} onValueChange={field.onChange} data-testid="select-data-type">
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="BOOL">BOOL</SelectItem>
                          <SelectItem value="INT">INT</SelectItem>
                          <SelectItem value="DINT">DINT</SelectItem>
                          <SelectItem value="REAL">REAL</SelectItem>
                          <SelectItem value="STRING">STRING</SelectItem>
                        </SelectContent>
                      </Select>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="scanInterval"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Scan Interval (ms)</FormLabel>
                    <FormControl>
                      <Input 
                        type="number" 
                        min="1000" 
                        step="500"
                        {...field} 
                        onChange={(e) => field.onChange(parseInt(e.target.value) || 2000)}
                        data-testid="input-scan-interval"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Description (Optional)</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Tag description..."
                        value={field.value || ""}
                        onChange={field.onChange}
                        onBlur={field.onBlur}
                        name={field.name}
                        ref={field.ref}
                        data-testid="input-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="isActive"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
                    <div className="space-y-0.5">
                      <FormLabel>Enable Tag</FormLabel>
                      <div className="text-sm text-muted-foreground">
                        Start monitoring this tag immediately
                      </div>
                    </div>
                    <FormControl>
                      <Switch
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="switch-is-active"
                      />
                    </FormControl>
                  </FormItem>
                )}
              />

              <div className="flex justify-end gap-2">
                <Button type="button" variant="outline" onClick={() => setIsTagDialogOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit" disabled={createTagMutation.isPending} data-testid="button-save-tag">
                  {createTagMutation.isPending ? "Saving..." : (editingTag ? "Update" : "Create")}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/plc-tag-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Upload, Download, Plus, Trash2, AlertTriangle, CheckCircle, Activity, Settings } from "lucide-react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface PlcTag {
  id: string;
  siteId: string;
  tagName: string;
  plcAddress: string;
  description: string;
  dataType: string;
  isActive: boolean;
  alarmOnTrue: boolean;
  alarmOnFalse: boolean;
  severityLevel: string;
  lastValue: string | null;
  lastReadTime: string | null;
  createdAt: string;
  updatedAt: string;
}

interface Site {
  id: string;
  name: string;
  ipAddress: string;
  status: string;
}

export default function PlcTagManagement() {
  const [selectedSite, setSelectedSite] = useState("");
  const [bulkUploadText, setBulkUploadText] = useState("");
  const [showBulkUpload, setShowBulkUpload] = useState(false);
  const { toast } = useToast();

  // Fetch sites
  const { data: sites } = useQuery<Site[]>({
    queryKey: ["/api/sites"],
  });

  // Fetch PLC tags
  const { data: plcTags, isLoading } = useQuery<PlcTag[]>({
    queryKey: ["/api/plc-tags"],
  });

  // Bulk upload mutation
  const bulkUploadMutation = useMutation({
    mutationFn: async (data: { siteId: string; tags: any[] }) => {
      return apiRequest("/api/plc-tags/bulk", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      setBulkUploadText("");
      setShowBulkUpload(false);
      toast({
        title: "Success",
        description: "PLC tags uploaded successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to upload PLC tags",
        variant: "destructive",
      });
    },
  });

  // Delete tag mutation
  const deleteTagMutation = useMutation({
    mutationFn: async (tagId: string) => {
      return apiRequest(`/api/plc-tags/${tagId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/plc-tags"] });
      toast({
        title: "Success",
        description: "PLC tag deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete PLC tag",
        variant: "destructive",
      });
    },
  });

  const handleBulkUpload = () => {
    if (!selectedSite || !bulkUploadText.trim()) {
      toast({
        title: "Error",
        description: "Please select a site and provide tag data",
        variant: "destructive",
      });
      return;
    }

    try {
      const lines = bulkUploadText.trim().split('\n');
      const tags = lines.map(line => {
        const parts = line.split(',').map(p => p.trim());
        if (parts.length < 4) {
          throw new Error(`Invalid line format: ${line}`);
        }
        return {
          tagName: parts[0],
          plcAddress: parts[1],
          description: parts[2],
          dataType: parts[3] || 'BOOL',
          alarmOnTrue: (parts[4] || 'true').toLowerCase() === 'true',
          severityLevel: parts[5] || 'warning'
        };
      });

      bulkUploadMutation.mutate({ siteId: selectedSite, tags });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Invalid CSV format",
        variant: "destructive",
      });
    }
  };

  const getSiteName = (siteId: string) => {
    const site = sites?.find(s => s.id === siteId);
    return site ? site.name : "Unknown Site";
  };

  const getTagIcon = (dataType: string, alarmOnTrue: boolean) => {
    if (alarmOnTrue) {
      return <AlertTriangle className="h-4 w-4 text-orange-500" />;
    }
    if (dataType === 'BOOL') {
      return <CheckCircle className="h-4 w-4 text-blue-500" />;
    }
    if (dataType === 'REAL' || dataType === 'INT') {
      return <Activity className="h-4 w-4 text-green-500" />;
    }
    return <Settings className="h-4 w-4 text-gray-500" />;
  };

  const filteredTags = selectedSite && selectedSite !== "all"
    ? plcTags?.filter(tag => tag.siteId === selectedSite) 
    : plcTags;

  return (
    <div className="p-4 space-y-4">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold">PLC Tag Management</h1>
          <p className="text-sm text-gray-500">Configure PLC tags for site monitoring and alerts</p>
        </div>
        <div className="flex gap-3">
          <Button 
            variant="outline" 
            onClick={() => setShowBulkUpload(!showBulkUpload)}
            className="flex items-center gap-2"
            data-testid="button-bulk-upload"
          >
            <Upload className="h-4 w-4" />
            Bulk Upload
          </Button>
        </div>
      </div>

      {/* Site Filter */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center gap-3">
            <Label htmlFor="site-select">Filter by Site:</Label>
            <Select value={selectedSite} onValueChange={setSelectedSite}>
              <SelectTrigger className="w-64" data-testid="select-site-filter">
                <SelectValue placeholder="All Sites" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Sites</SelectItem>
                {sites?.map(site => (
                  <SelectItem key={site.id} value={site.id}>
                    {site.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {filteredTags && (
              <Badge variant="outline" className="ml-2">
                {filteredTags.length} tags
              </Badge>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Bulk Upload Section */}
      {showBulkUpload && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Upload className="h-5 w-5" />
              Bulk Upload PLC Tags
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="site-upload">Select Site for Upload:</Label>
              <Select value={selectedSite} onValueChange={setSelectedSite}>
                <SelectTrigger className="w-full mt-1" data-testid="select-site-upload">
                  <SelectValue placeholder="Choose a site..." />
                </SelectTrigger>
                <SelectContent>
                  {sites?.map(site => (
                    <SelectItem key={site.id} value={site.id}>
                      {site.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="bulk-data">CSV Data (tagName, plcAddress, description, dataType, alarmOnTrue, severityLevel):</Label>
              <Textarea
                id="bulk-data"
                placeholder={`Example format:
CTS Feed Pump 1 Trip, GVL.M_CTS_FEED_PUMP1_TRIP, CTS Feed Pump 1 Trip Alarm, BOOL, true, critical
CTS Feed Pump 2 Trip, GVL.M_CTS_FEED_PUMP2_TRIP, CTS Feed Pump 2 Trip Alarm, BOOL, true, critical
Flow Rate Inlet, GVL.FLOW_RATE_INLET, Inlet Flow Rate, REAL, false, info`}
                value={bulkUploadText}
                onChange={(e) => setBulkUploadText(e.target.value)}
                className="mt-1 min-h-32"
                data-testid="textarea-bulk-upload"
              />
            </div>

            <div className="flex gap-2">
              <Button 
                onClick={handleBulkUpload}
                disabled={bulkUploadMutation.isPending || !selectedSite || !bulkUploadText.trim()}
                className="flex items-center gap-2"
                data-testid="button-confirm-upload"
              >
                <Upload className="h-4 w-4" />
                {bulkUploadMutation.isPending ? "Uploading..." : "Upload Tags"}
              </Button>
              <Button 
                variant="outline" 
                onClick={() => setShowBulkUpload(false)}
                data-testid="button-cancel-upload"
              >
                Cancel
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* PLC Tags List */}
      <Card>
        <CardHeader>
          <CardTitle>PLC Tags</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-16 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
              ))}
            </div>
          ) : filteredTags && filteredTags.length > 0 ? (
            <div className="space-y-3">
              {filteredTags.map((tag) => (
                <div key={tag.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50 dark:hover:bg-gray-800" data-testid={`tag-row-${tag.id}`}>
                  <div className="flex items-center space-x-3 flex-1">
                    <div className="flex items-center justify-center w-8 h-8 rounded-full bg-gray-100 dark:bg-gray-700">
                      {getTagIcon(tag.dataType, tag.alarmOnTrue)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-1">
                        <h3 className="text-sm font-semibold text-gray-900 dark:text-gray-100 truncate">
                          {tag.tagName}
                        </h3>
                        <Badge variant="outline" className="text-xs">
                          {tag.dataType}
                        </Badge>
                        {tag.alarmOnTrue && (
                          <Badge variant="outline" className="text-xs bg-orange-50 text-orange-700 dark:bg-orange-900 dark:text-orange-300">
                            Alarm
                          </Badge>
                        )}
                        <Badge variant="outline" className="text-xs bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-300">
                          {tag.severityLevel}
                        </Badge>
                      </div>

                      <p className="text-xs text-gray-600 dark:text-gray-400 mb-1">
                        {tag.description}
                      </p>

                      <div className="flex items-center gap-3 text-xs text-gray-500">
                        <span className="font-mono">{tag.plcAddress}</span>
                        <span>Site: {getSiteName(tag.siteId)}</span>
                        {tag.lastValue && (
                          <span>Last: {tag.lastValue}</span>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <Badge variant={tag.isActive ? "default" : "secondary"} className="text-xs">
                      {tag.isActive ? "Active" : "Inactive"}
                    </Badge>

                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => deleteTagMutation.mutate(tag.id)}
                      disabled={deleteTagMutation.isPending}
                      className="text-red-600 hover:text-red-700 dark:text-red-400 dark:hover:text-red-300"
                      data-testid={`button-delete-${tag.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <Settings className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p>No PLC tags configured</p>
              <p className="text-sm">Use bulk upload to add tags for your sites</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

